package com;

public class OneDimensionalArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int arr[]=new int [5];
		int count=0;
		
		for(int i=0;i<arr.length;i++)
		{
			arr[i]=count;
			count++;
		System.out.println(arr[i]+" ");
		}
				
}	
		}
		
		
		
		
		
		
		
		
		

	


